import React from 'react';
import logo from './logo.svg';
import './App.css';
import {binarySearch} from './helpers/search'

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {input: "", search: ""}
  }
  handleChange = (e) => {
    const input = e.target.value
    const change = e.target.name
    console.log(e.target.name)
    this.setState({[change]: input})
  }

  makeArray = (string) => {
    return string.split(' ')
  }

  handleSubmit = (e) => {
    e.preventDefault()
    binarySearch(
      this.makeArray(
        this.state.input))
  }
  render(){
    return (
      <div className="App">
        <header className="App-header">
          <form
            onSubmit={(e) => this.handleSubmit(e)}
          >
            <label htmlFor="">Input :
              <textarea 
                id="input"
                name="input"
                cols="30" 
                rows="10" 
                value={this.state.input}
                onChange={(e) => this.handleChange(e)}
              >
              </textarea>
            </label>
            <label htmlFor="">
              Search For: 
              <input 
                type="text" 
                name="change"
                onChange={(e) => this.handleChange(e)}
              />
            </label>
            <button>Submit</button>
          </form>
        </header>
      </div>
    );
  }
}

export default App;
